import React from 'react';
import {toast} from 'react-toastify';
import AuthenticationService
  from '../../../auth/services/AuthenticationService';
import {Redirect} from 'react-router-dom';
import '../../../../node_modules/bootstrap/dist/css/bootstrap.min.css';
import "../../../assets/login/login.css";

class LoginForm extends React.Component {

  state = {
    redirectToReferrer: false,
  };

  //Evento que se dispara cuando ejecutamos el Submit del login
  handleLoginAction = (event) => {
    event.preventDefault();

    //Si los campos del formulario estan vacioes
    if (!event.target.checkValidity()) {
      toast.error('Please, fill all the required fields!');
      return;
    }

    const data = new FormData(event.target);
    const userName = data.get('username');
    const password = data.get('password');

    //Generamos el session storege
    AuthenticationService.login(userName, password).then(() => {     

      this.setState(() => ({
        redirectToReferrer: true,
      }));

    }).catch(error => {
      console.error(error);
      toast.error(`Couldn't start a session!`);
    });
  };

  render() {

    const {from} = {from: {pathname: '/users'}};
    const {redirectToReferrer} = this.state;
    //si se inicio sesion hacemos un redirect al content
    if (redirectToReferrer) {
      return <Redirect to={from}/>;
    }

    return (
        // <div className="col-12 col-sm-12 col-md-6 offset-md-3">
        //   <div className="b-form">
        //     <h1>Login</h1>
        //     <form onSubmit={this.handleLoginAction} noValidate>
        //       <div className="form-group">
        //         <label htmlFor="username">Usuario :</label>
        //         <input id="username" name="username" className="form-control"
        //                type="text" required
        //                defaultValue={'usuario'}/>
        //       </div>
        //       <div className="form-group">
        //         <label htmlFor="password">Contraseña :</label>
        //         <input id="password" name="password" className="form-control"
        //                type="password" required defaultValue={'contraseña'}/>
        //       </div>

        //       <button type="submit" className="btn btn-primary">Ingresar
        //       </button>
        //     </form>
        //   </div>
        // </div>
        <div className="col-12 col-sm-12 col-md-12 offset-md-4">
        <div className="container-fluid">
        <div className="row">
        <div className="col-md-4 col-md-offset-4">
        <div id="login-div">
          <div className="row">
            <div className="col-md-12">
              <h5 className="page-header text-uppercase" >Iniciar sesi&oacute;n</h5>
            </div>
          </div>
          <form onSubmit={this.handleLoginAction} noValidate>
            <div className="row">
              <div className="col-md-12">
                <div className="form-group has-feedback">
                  <label for="user_name" className="control-label">Usuario</label>
                  <input type="text" id="username" name="username" maxlength="10" 
                        className="form-control" autofocus autocomplete="off" 
                        type="text" required
                        defaultValue={'usuario'}/>
                  <span className="glyphicon glyphicon-remove form-control-feedback hide"></span>
                  {/* <p className="text-center help-block hide">Debe ingresar su usuario.</p> */}
                </div>
              </div>
            </div>
            <div className="row">
              <div className="col-md-12">
                <div className="form-group has-feedback">
                  <label for="contrasenia" className="control-label">Contrase&ntilde;a</label>
                  <input type="password" name="contrasenia" id="contrasenia" maxlength="32" className="form-control"/>
                  <span className="glyphicon glyphicon-remove form-control-feedback hide"></span>
                  {/* <p className="help-block text-center hide">Debe ingresar su contrase&ntilde;a.</p> */}
                </div>
              </div>
            </div>
            <div className="row">
              <div className="col-md-12 text-right">
                <input type="submit" id="btn-enviar" value="Iniciar sesi&oacute;n" className="btn btn-primary"/>
              </div>
            </div>
            </form>
        </div>
        </div>
        </div>
        </div>
        </div>
    );
  }
}

export default LoginForm;
