import React from 'react';
import UsersService from './services/UsersService';
import {ToastContainer,toast} from 'react-toastify';

class UsersTable extends React.Component {
  state = {users: []};
  handleClickOnIconToDeleteUser = (user) => {
    // This function is passed as "prop"
    // See more about in: https://www.javascriptstuff.com/component-communication/#3-callback-functions
    this.props.onDeleteUser(user);
  };

  handleClickOnIconToUpdateUserStatus = (user) => {
    // This function is passed as "prop"
    user.status = user.status === 'ACTIVE' ? 'INACTIVE' : 'ACTIVE';
    this.props.onUpdateUserStatus(user);
  };
  componentDidMount() {
    UsersService.getAllUsers().then((response) => {
      this.setState({users: response.data});
    });
  }

  removeUserFromCurrentState = (userToDelete) => {

    this.setState(
        prevState => ({
          users: prevState.users.filter(
              user => user.id !== userToDelete.id),
        }));
  };

  handleClickDeleteUser = (user) => {

    UsersService.deleteUser(user).then((response) => {
      const user = response.data;
      const {name, email} = user;
      toast.success(`User deleted: ${name} (${email})`);

      // Delete user from current state
      this.removeUserFromCurrentState(user);
    });
  };
  render() {

    // const {users} = this.props;
    const {users} = this.state;
    return (
        <div className="b-content container">
          <ToastContainer position="top-center"
            autoClose={8000}
            hideProgressBar={false}
            newestOnTop={false}
            closeOnClick
            rtl={false}
            pauseOnFocusLoss
            draggable
            pauseOnHover/>
          <h1>Listado de Usuarios</h1>
          <table className="table">
            <thead className="thead-dark">
            <tr>
              <th scope="col">ID</th>
              <th scope="col">Nomnre</th>
              <th scope="col">Email</th>
              <th scope="col">Usuario</th>
              <th scope="col">Actions</th>
            </tr>
            </thead>
            <tbody>

            {users.map(user => (
                <tr key={user.id}>
                  <th scope="row">{user.id}</th>
                  <td>{user.name}</td>
                  <td>{user.email}</td>
                  <td>{user.username}</td>
                  <td>
                    <button className="btn btn-danger"
                            onClick={() => this.handleClickDeleteUser(
                                user)}>
                      <i className="b-delete far fa-trash-alt"></i>
                    </button>
                  </td>
                </tr>))
            }

            </tbody>
          </table>
        </div>
    );
  }
}

// UsersTable.propTypes = {
//   onDeleteUser: React.PropTypes.func,
// };

export default UsersTable;
